mapper.select:
        SELECT
        cc.*,
        cc.COUPON_CATEGORY as couponCategoryId,
		ccc.CATEGORY_NAME,
		cd_company.id as companyId,
		cd_company.COMPANY_NAME,
        r.cd_member_id AS memberId,
		su.realname as managerName,
		su.id as managerId,
		price.ref_value as receivedPrice
        FROM
        cd_coupon cc
        left join cd_coupon_category ccc on cc.COUPON_CATEGORY = ccc.id
        left join cd_company cd_company on cc.cd_company_id = cd_company.id
        left join sys_users su on cc.cd_user_id = su.id
        LEFT JOIN cd_member_coupon_relation r ON r.COUPON_NUMBER = cc.COUPON_NUMBER
        left join cd_coupon_ref price on cc.id = price.cd_coupon_id and price.ref_type='RECEIVED_PRICE'

       			 (cc.IS_USED = 0 or cc.IS_USED = 1)   

                  AND (cc.COUPON_NUMBER LIKE CONCAT('%', #{keyword}, '%') or cc.COUPON_DESC LIKE CONCAT('%', #{keyword}, '%'))


                  AND cc.COUPON_NUMBER = #{couponNumber}


              	AND ccc.category_type = #{categoryType}


              	AND cc.COUPON_TYPE = #{couponType}


              	AND cc.COUPON_CATEGORY = #{couponCategoryId}


              	AND cc.is_send = #{isSend}


              	AND cc.batch = #{batch}


              	AND cc.is_useful = #{isUseful}


              	AND cc.is_useful = 0 and cc.coupon_Value > 0 AND cc.PAYMENT_TYPE = 1 AND cc.coupon_type = 1


              	AND ( cc.coupon_type = 2 or (cc.PAYMENT_TYPE = 2 and cc.COUPON_TYPE =1))


                AND cc.cd_user_id = #{customerMangerId}


              	AND cc.cd_company_id = #{cdCompanyId}


              	AND cc.cd_user_id = #{managerId}


              	AND cc.is_over = #{isOver}



	                  AND DATE_FORMAT(cc.AUDIT_DATE ,'%Y-%m-%d')>= #{auditDateStart}




	                  AND DATE_FORMAT(cc.AUDIT_DATE ,'%Y-%m-%d')<= #{auditDateEnd}

        SELECT
        	cc.id,
			car.CD_ACTIVITY_ID,
	        cc.COUPON_TYPE,
	        cc.USER_PRICE,
	        cc.COUPON_NUMBER,
	        cc.BATCH,
	        cc.COUPON_CATEGORY as couponCategoryId,
			ccc.CATEGORY_NAME,
			cd_company.COMPANY_NAME,
	        r.CD_MEMBER_ID AS memberId,
			su.realname as managerName,
			price.ref_value as receivedPrice
        FROM
	        cd_coupon cc
	        left join cd_coupon_category ccc on cc.COUPON_CATEGORY = ccc.id
	        left join cd_company cd_company on cc.cd_company_id = cd_company.id
	        left join sys_users su on cc.cd_user_id = su.id
	        left join cd_coupon_ref price on cc.id = price.cd_coupon_id and price.ref_type='RECEIVED_PRICE'
	        LEFT JOIN cd_member_coupon_relation r ON r.COUPON_NUMBER = cc.COUPON_NUMBER
			left join cd_activity_ref car on cc.COUPON_CATEGORY = car.REF_VALUE and car.REF_TYPE ='COUPON'

       			 (cc.IS_USED = 0 or cc.IS_USED = 1)   

                  AND (cc.COUPON_NUMBER LIKE CONCAT('%', #{keyword}, '%'))


                  AND cc.COUPON_NUMBER = #{couponNumber}


              	AND ccc.category_type = #{categoryType}


              	AND cc.COUPON_TYPE = #{couponType}


              	AND cc.COUPON_CATEGORY = #{couponCategoryId}


              	AND cc.is_send = #{isSend}


              	AND cc.batch = #{batch}


              	AND cc.is_useful = #{isUseful}


              	AND cc.is_useful = 0 and cc.coupon_Value > 0


                AND cc.cd_user_id = #{customerMangerId}


              	AND cc.cd_company_id = #{cdCompanyId}


              	AND cc.cd_user_id = #{managerId}


              	AND cc.is_over = #{isOver}

SELECT cc.id, car.CD_ACTIVITY_ID, cc.COUPON_TYPE, cc.USER_PRICE, cc.COUPON_NUMBER, cc.BATCH, cc.COUPON_CATEGORY as couponCategoryId, ccc.CATEGORY_NAME, cd_company.COMPANY_NAME, r.CD_MEMBER_ID AS memberId, su.realname as managerName, price.ref_value as receivedPrice, activity.LOWEST_PRICE FROM cd_coupon cc left join cd_coupon_category ccc on cc.COUPON_CATEGORY = ccc.id left join cd_company cd_company on cc.cd_company_id = cd_company.id left join sys_users su on cc.cd_user_id = su.id left join cd_coupon_ref price on cc.id = price.cd_coupon_id and price.ref_type='RECEIVED_PRICE' LEFT JOIN cd_member_coupon_relation r ON r.COUPON_NUMBER = cc.COUPON_NUMBER left join cd_activity_ref car on cc.COUPON_CATEGORY = car.REF_VALUE and car.REF_TYPE ='COUPON' left join cd_activity activity on car.CD_ACTIVITY_ID = activity.id
(cc.IS_USED = 0 or cc.IS_USED = 1) and car.cd_Activity_Id != 0 and cc.user_price > 0
AND (cc.COUPON_NUMBER LIKE CONCAT('%', #{keyword}, '%'))
AND cc.COUPON_NUMBER = #{couponNumber}
AND ccc.category_type = #{categoryType}
AND cc.COUPON_TYPE = #{couponType}
AND cc.COUPON_CATEGORY = #{couponCategoryId}
AND cc.is_send = #{isSend}
AND cc.batch = #{batch}
AND cc.is_useful = #{isUseful}
AND cc.is_useful = 0 and cc.coupon_Value > 0
AND cc.cd_user_id = #{customerMangerId}
AND cc.cd_company_id = #{cdCompanyId}
AND cc.cd_user_id = #{managerId}
AND cc.is_over = #{isOver}

select cc.COUPON_NUMBER as couponNo, cc.USER_PRICE as couponPrice, cm.`NAME` as userName, cm.PHONE_NUMBER, cc.COUPON_PASSWORD from cd_coupon cc left join cd_member_coupon_relation cmcr on cc.COUPON_NUMBER = cmcr.COUPON_NUMBER left join cd_member cm on cmcr.CD_MEMBER_ID = cm.ID
AND cc.COUPON_NUMBER = #{couponNumber}
AND cc.COUPON_PASSWORD = #{password}
