mapper.select:
select cc.* from cd_express cc
AND cc.express_code LIKE CONCAT('%', #{keyword}, '%') or cc.express_name LIKE CONCAT('%', #{keyword}, '%')
AND cc.id = #{id}

select torp.REF_VALUE as couponNumber, sp.SHOW_VALUE AS orderTypeDesc, ssp.SHOW_VALUE AS orderStatusDesc, paymentp.SHOW_VALUE AS paymentModeDesc, osp.SHOW_VALUE AS orderSourceDesc, ce.express_name, users.realname as manager_name, tb.* FROM tb_order_ref tor left join tb_order tb on tb.order_no = tor.order_no LEFT JOIN sys_dictionary s on s.DICTIONARY_CODE='ORDER_TYPE' left join sys_dictionary_parameter sp on sp.SYS_DICTIONARY_ID=s.ID and tb.ORDER_TYPE = sp.REAL_VALUE
LEFT JOIN sys_dictionary ss on ss.DICTIONARY_CODE='ORDER_STATUS' left join sys_dictionary_parameter ssp on ssp.SYS_DICTIONARY_ID=ss.ID and tb.ORDER_STATUS = ssp.REAL_VALUE
LEFT JOIN sys_dictionary payment on payment.DICTIONARY_CODE='PAYMENT_MODE' left join sys_dictionary_parameter paymentp on paymentp.SYS_DICTIONARY_ID=payment.ID and tb.PAYMENT_MODE = paymentp.REAL_VALUE
LEFT JOIN sys_dictionary oss on oss.DICTIONARY_CODE='ORDER_SOURCE' left join sys_dictionary_parameter osp on osp.SYS_DICTIONARY_ID=oss.ID and tb.ORDER_SOURCE = osp.REAL_VALUE
LEFT JOIN tb_order_relation torp ON torp.ORDER_NO = tb.ORDER_NO AND torp.REF_TYPE = 'ORDER_PAY_RELATION' left join cd_express ce on tb.despatch_express_id = ce.id
left join sys_users users on users.id = tb.MANAGER_ID
tor.ref_type ='TAKE_DELIVERY' AND tb.order_status = 12
AND tb.order_status = #{orderStatus}
AND (tb.order_no LIKE CONCAT('%', #{keyword}, '%') or tb.consignee_phone LIKE CONCAT('%', #{keyword}, '%') or tb.consignee_name like CONCAT('%', #{keyword}, '%') )
AND tb.id = #{id}
AND tb.MANAGER_ID = #{managerId}
AND tb.consignee_name LIKE CONCAT('%', #{consigneeName}, '%')
AND tb.ORDER_NO = #{orderNo}
AND tb.ORDER_NO IN
#{item}
AND DATE_FORMAT(tb.APPOINTMENT_TIME ,'%Y-%m-%d')>= #{orderCreateTimeStart}
AND DATE_FORMAT(tb.APPOINTMENT_TIME ,'%Y-%m-%d')<= #{orderCreateTimeEnd}
AND tb.create_user_id = #{createUserId}
AND ce.EXPRESS_CODE =#{despatchExpressCode}
order by tb.CREATE_TIME desc

select paymentp.SHOW_VALUE AS paymentModeDesc, otherPaymentp.show_value as otherPaymentModeDesc, ce.express_name, tb.create_time, tb.order_no, tb.consignee_name, tb.consignee_phone, tb.consignee_address, if(tb.PAYMENT_MODE=4,tb.TOTAL_AMOUNT,null) TOTAL_AMOUNT, if(tb.PAYMENT_MODE=4,pay.AMOUNT,null) payAmount, if(tb.PAYMENT_MODE=4,other_pay.AMOUNT,null) otherPayAmount, tb.appointment_time, tb.remark FROM tb_order tb
LEFT JOIN sys_dictionary payment on payment.DICTIONARY_CODE='PAYMENT_MODE' left join sys_dictionary_parameter paymentp on paymentp.SYS_DICTIONARY_ID=payment.ID and tb.PAYMENT_MODE = paymentp.REAL_VALUE
LEFT JOIN sys_dictionary otherPayment on otherPayment.DICTIONARY_CODE='PAYMENT_MODE' left join sys_dictionary_parameter otherPaymentp on otherPaymentp.SYS_DICTIONARY_ID=otherPayment.ID and tb.PAYMENT_MODE = otherPaymentp.REAL_VALUE
left join cd_express ce on tb.despatch_express_id = ce.id LEFT JOIN TB_TRADE_RECORD pay ON pay.TRADE_NO =tb.ORDER_NO and pay.TRADE_TYPE in('COUPON','CARD','NORMAL') LEFT JOIN TB_TRADE_RECORD other_pay ON other_pay.TRADE_NO =tb.ORDER_NO and other_pay.TRADE_TYPE = tb.OTHER_PAYMENT_MODE
tb.order_status = 12
AND DATE_FORMAT(tb.APPOINTMENT_TIME ,'%Y-%m-%d')>= #{orderCreateTimeStart}
AND DATE_FORMAT(tb.APPOINTMENT_TIME ,'%Y-%m-%d')<= #{orderCreateTimeEnd}
AND ce.EXPRESS_CODE =#{despatchExpressCode}
order by tb.CREATE_TIME desc

select paymentp.SHOW_VALUE AS paymentModeDesc, otherPaymentp.show_value as otherPaymentModeDesc, ce.express_name, tb.create_time, tb.order_no, tb.consignee_name, tb.consignee_phone, tb.consignee_address, if(tb.PAYMENT_MODE=4,tb.TOTAL_AMOUNT,null) TOTAL_AMOUNT, if(tb.PAYMENT_MODE=4,pay.AMOUNT,null) payAmount, if(tb.PAYMENT_MODE=4,other_pay.AMOUNT,null) otherPayAmount, tb.appointment_time, tb.remark, ssp.SHOW_VALUE AS orderStatusDesc, tb.total_box_num, tb.EXPRESS_SCAN_BOX_NUM FROM tb_order tb
LEFT JOIN sys_dictionary ss on ss.DICTIONARY_CODE='ORDER_STATUS' left join sys_dictionary_parameter ssp on ssp.SYS_DICTIONARY_ID=ss.ID and tb.ORDER_STATUS = ssp.REAL_VALUE
LEFT JOIN sys_dictionary payment on payment.DICTIONARY_CODE='PAYMENT_MODE' left join sys_dictionary_parameter paymentp on paymentp.SYS_DICTIONARY_ID=payment.ID and tb.PAYMENT_MODE = paymentp.REAL_VALUE
LEFT JOIN sys_dictionary otherPayment on otherPayment.DICTIONARY_CODE='PAYMENT_MODE' left join sys_dictionary_parameter otherPaymentp on otherPaymentp.SYS_DICTIONARY_ID=otherPayment.ID and tb.PAYMENT_MODE = otherPaymentp.REAL_VALUE
left join cd_express ce on tb.despatch_express_id = ce.id LEFT JOIN TB_TRADE_RECORD pay ON pay.TRADE_NO =tb.ORDER_NO and pay.TRADE_TYPE in('COUPON','CARD','NORMAL') LEFT JOIN TB_TRADE_RECORD other_pay ON other_pay.TRADE_NO =tb.ORDER_NO and other_pay.TRADE_TYPE = tb.OTHER_PAYMENT_MODE
tb.DESPATCH_EXPRESS_ID is not NULL and tb.ORDER_STATUS in(1,2,3,4,13)
AND DATE_FORMAT(tb.APPOINTMENT_TIME ,'%Y-%m-%d')>= #{orderCreateTimeStart}
AND DATE_FORMAT(tb.APPOINTMENT_TIME ,'%Y-%m-%d')<= #{orderCreateTimeEnd}
AND ce.EXPRESS_CODE =#{despatchExpressCode}
order by tb.CREATE_TIME desc
