mapper.select:
SELECT ttp.*, cp.plan_name, cp.delivery_type, cp.delivery_time, cc.id as company_id, cc.company_name, su.realName as managerName, su.id as manager_id FROM tb_table_plan ttp left join cd_plan cp on ttp.cd_plan_id = cp.id left join cd_coupon cm on ttp.coupon_number = cm.coupon_number left join cd_company cc on cm.cd_company_id = cc.id left join sys_users su on cm.cd_user_id = su.id
AND (ttp.COUPON_NUMBER LIKE CONCAT('%', #{keyword}, '%') or ttp.consignee_Name LIKE CONCAT('%', #{keyword}, '%') or ttp.CONSIGNEE_PHONE like CONCAT('%', #{keyword}, '%'))
AND ttp.id = #{id}
AND cm.cd_user_id = #{userId}
